import React,{useState, useEffect} from "react";
import styled from "styled-components";
import Logout from "./Logout";
import ChatInput from "./ChatInput";
import Messages from "./Messages";
import axios from "axios";
import { getAllMessagesRoute, sendMessageRoute } from "../utils/APIRoutes";

export default function ChatContainer({currentChat, currentUser, socket}) {
    const [messages, setMessages] = useState([]);
    const [arrivalMessage, setArrivalMessage] = useState(null);
    const scrollRef = useRef();
    useEffect(() => {
        const fetchData = async () => {
          try {
            if(currentChat) {
                const response = await axios.post(getAllMessagesRoute, {
                    from: currentUser._id,
                    to: currentChat._id,
                });
                setMessages(response.data);
            }
          } catch (error) {
            // Handle the error, e.g., display an error message or retry the request.
            console.error("Error fetching messages:", error);
          }
        };
        fetchData();
    }, [currentChat]);

    const handleSendMsg = async(msg) => {
        await axios.post(sendMessageRoute, {
            from: currentUser._id,
            to: currentChat._id,
            message: msg,
        });
        socket.current.emit("send-msg", {
            to: currentChat._id,
            from: currentUser._id,
            message: msg,
        })
        const msgs = [...messages];
        msg.push({fromSelf:true,message:msg});
        setMessages(msgs);
    };
    useEffect(() => {
        if(socket.current) {
            socket.current.on("msg-recieve", (msg)=> {
                setArrivalMessage({fromSelf:false, message:msg});
            });
        }
    }, []);

    useEffect(()=> {
        arrivalMessage && setMessages((prev)=>[...prev,arrivalMessage]);
    },[arrivalMessage])

    useEffect(()=> {
        scrollRef.current?.scrollIntoView({behavious: "smooth"});
    }, [messages]);

    return (
        <>
            {currentChat && (
                <Container>
                    <div className="chat-header">
                        <div className="user-details">
                            <div className="avatar">
                                <img 
                                    src={`data:image/svg+xml;base64,${currentChat.avatarImage}`}
                                    alt="avatar"
                                />
                            </div>
                            <div className="username">
                                <h3>{currentChat.username}</h3>
                            </div>
                        </div>
                        <Logout />
                    </div>
                    <div className="chat-messages">
                        {
                            messages.map((message) => {
                                return (
                                    <div>
                                        <div className= {`message ${message.fromSelf ? "sent":"recieved"}`}>
                                            <div className="content">
                                                <p>
                                                    {message.message}
                                                    {message.emotion && (
                                                        <span role="img" aria-label="emotion">
                                                            {message.emotion}
                                                        </span>
                                                    )}
                                                </p>
                                            </div>
                                        </div>
                                    </div>
                                )
                            }
                        )}
                    </div>
                    <ChatInput handleSendMsg={handleSendMsg}/>
                </Container>
            )}
        </>
    );
}

const Container = styled.div
`
    padding-top: 1rem;
    display: grid;
    grid-template-rows: 10% 78% 12%;
    gap: 0.1rem;
    overflow: hidden;
    @media screenand (min-width: 720px) and (max:width: 1080px) {
        grid-auto-rows: 15% 70% 15%;
    }
    .chat-header {
        display:flex;
        justify-content: space-between;
        align-items: center;
        padding: 0 2rem;
        .user-details {
            display: flex;
            align-items: center;
            gap: 1rem;
            .avatar {
                img {
                    height: 3rem;
                }
            }
            .username {
                h3 {
                    color: white;
                }
            }
        }
    }
    .chat-messages {
        padding: 1rem 2rem;
        display: flex;
        flex-direction: column;
        gap: 1rem;
        overflow: auto;
        .message {
            display: flex;
            align-items: center;
            .content {
                max-width: 40%;
                overflow-wrap; break-word;
                padding: 1rem;
                font-size: 1.1rem;
                border-radius: 1rem;
                color: #d1d1d1;
            }
        }
        .sent {
            justify-content: flex-end;
            .content {
                background-color: #9e7572;
            }
        }
        .recieved {
            justify-content: flex-start;
            .content {
                background-color: #5c5858;
            }
        }
    }
`;
