import React, { useState, useEffect } from 'react';
import styled from "styled-components";
import { Link, useNavigate } from 'react-router-dom';
import Logo from "../assets/logo.png";
import { ToastContainer, toast } from "react-toastify";
import 'react-toastify/dist/ReactToastify.css';
import axios from "axios";
import { loginRoute } from "../utils/APIRoutes";

function Login() {
    const navigate = useNavigate();
    const [values, setValues] = useState({
        username: "",
        password: "",
    });
    const toastOptions =  {
        position: "bottom-right",
        autoClose: 8000,
        pauseOnHover: true,
        draggable: true,
        theme: "dark",
    }
  const handleSubmit = async(event)=> {
    event.preventDefault();
    if(handleValidation()) {
        const {password, username,} = values;
        const {data} = await axios.post(loginRoute, {
            username,
            password,
        });
        if(data.status === false) {
            toast.error(data.msg, toastOptions);
        }
        if(data.status === true) {
            localStorage.setItem('chat-app-user',JSON.stringify(data.user));
            navigate("/");
        }
    }
  };

  const handleValidation =() => {
    const {password, username} = values;
    if(password === "") {
        toast.error("Your password is required", toastOptions);
        return false;
    } else if (username.length === "") {
        toast.error("Your username username required", toastOptions);
        return false;
    } 
    return true;
  };

  const handleChange = (event) => {
    setValues({ ...values, [event.target.name]: event.target.value });
  };

  return (
    <>
        <FormContainer>
            <form onSubmit={(event)=>handleSubmit(event)}>
                <div className='brand'>
                    <img src={Logo} alt="Logo" />
                    <h1>eMotion</h1>
                </div>
                <input 
                    type='text' 
                    placeholder="Username" 
                    name="username" 
                    onChange={(e) =>handleChange(e)}
                    min="3"
                />
                <input 
                    type='password' 
                    placeholder="Password" 
                    name="password" 
                    onChange={(e) =>handleChange(e)}
                />
                <button type="submit">Login</button>
                <span>
                    Don't have an account? <Link to="/register">Register</Link>
                </span>
            </form>
        </FormContainer>
        <ToastContainer/>
    </>
  );

}

const FormContainer = styled.div`
    height: 100vh;
    width: 100vw;
    display: flex;
    flex-direction: column;
    justify-content: center;
    gap: 1rem;
    align-items: center;
    background-color: #4b77bd;
    .brand {
        display: flex;
        align-items: center;
        gap: 1rem;
        justify-content: center;
        img {
            height: 5rem;
        }
        h1 {
            color: white;
        }
    }
    form {
        display: flex;
        flex-direction: column;
        gap: 2rem;
        background-color: #395a8f;
        border-radius: 2rem;
        padding: 3rem 5rem;
        input {
            background-color: transparent;
            padding: 1rem;
            border: 0.1rem solid #4b77bd;
            border-radius: 0.4rem;
            color: white;
            width: 100%;
            font-size: 1rem;
            &:focus {
                border: 0.1rem solid #768294;
                outline: none;
            }
        }
        button {
            background-color: #4b77bd;
            color: white;
            padding: 1rem 2rem;
            border: none;
            font-weight: bold;
            cursor: pointer;
            border-radius: 0.4rem;
            font-size: 1rem;
            text-transform: uppercase;
            transition: 0.4s ease-in-out;
            &:hover {
                background-color: #768294;
            }
        }
        span {
            color: white;
            //text-transform: uppercase;
            a {
                color: #768294;
                text-decoration: none;
                font-weight: bold
            }
        }
    }
`;

export default Login;
